value.stringValue:
OR(
      ISNEW(),
     (
         AND(
                  ISCHANGED([Opportunity].CloseDate),
                  (
                      OR(
                            ISPICKVAL([Opportunity].StageName, '1 - Lead'),
                            ISPICKVAL([Opportunity].StageName, '2 - Develop')
                      )
                  )
         )
     )
)

