WebLink.url:
var clw = window.open('/apex/SingletrackCMS__ManageContactTRData?id={!Contact.Id}', 'manage_contact', 'height=600,width=1000,resizeable=yes,location=no,toolbar=no,menubar=no,scrollbars=yes'); 

function checkWindow() 
{ 
if (clw && !clw.closed) { 
setTimeout(checkWindow, 100); 
} else { 
window.location.reload(); 
} 
} 

setTimeout(checkWindow, 100);
