WebLink.url:
var clw = window.open('/apex/SingletrackCMS__PickContactAddress?id={!Contact.Id}', 'pick_address', 'height=600,width=800,resizeable=1,location=0,menubar=0');

function checkWindow()
{
if (clw && !clw.closed) { 
setTimeout(checkWindow, 100); 
} else { 
window.location.reload();
} 
}

setTimeout(checkWindow, 100);
